mapper.select:
select 	a.*, 
		b.*, 
		f_cdnm('HireCode', emp01_hire_cd) emp01_hire_nm, 
		f_cdnm('RetireCode', emp01_retire_cd) emp01_retire_nm
from 	emp01_hire a, 
		sys02_user b 
where	emp01_user_id = sys02_user_id
and		sys02_company_id = #{companyId}
and		sys02_kor_nm like '%' + #{korName} + '%'
and		emp01_hire_date  < =  #{offDate}
and		NULLIS(emp01_retire_date, sysdate) > = #{onDate}
order by sys02_kor_nm
