mapper.select:
 
	select 	*
	from 	sys07_user_company, 
			sys01_company
	where 	sys07_company_id = sys01_company_id
	and	sys07_user_company_id = #{userCompanyId}

 
	select 	*
	from 	sys07_user_company, 
			sys01_company
	where 	sys07_company_id = sys01_company_id
	and		sys07_user_id = #{userId}

 
	select 	*
	from 	sys07_user_company, 
			sys01_company
	where 	sys07_company_id = sys01_company_id
	and		sys07_company_id = #{companyId}
