mapper.select:
select 	*
from 	sys06_menu,
		sys07_role_menu
where	sys06_menu_id = sys07_menu_id
and		sys07_role_menu_id = #{roleMenuId}

mapper.insert:
insert 	into	sys07_role_menu
	(	sys07_role_menu_id
	, 	sys07_role_id
	, 	sys07_menu_id
	, 	sys07_use_yn
	, 	sys07_note )
values 
	(	#{roleMenuId}
	, 	#{roleId}
	, 	#{menuId}
	, 	#{useYn}
	, 	#{note} )

mapper.update:
update 	sys07_role_menu
set		sys07_use_yn = #{useYn}
where	sys07_role_menu_id = #{roleMenuId}
